sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var caller = g_form.getValue('caller_id');

    var checkUserVip = new GlideAjax('incidentUtilsClient');
    checkUserVip.addParam('sysparm_name', 'getCallerifVip');
    checkUserVip.addParam('sysparm_caller', caller);
    checkUserVip.getXMLAnswer(handlerResponse);

    function handlerResponse(response) {

        var isVip = response;

		if(isVip == 'true'){

			g_form.addInfoMessage('O caller definido no incidente é um usuário VIP!');

		} else{

			//g_form.addInfoMessage('O caller definido no incidente NÃO é um usuário VIP!');
		}
    }

}
